mapper.select:
SELECT count(1) FROM activity_info
activity_name = #{activityName}
and date_format(activity_begin_date, '%Y-%m-%d') = #{logdate}
and activity_begin_date BETWEEN str_to_date(#{starttime}, '%Y-%m-%d') AND str_to_date(#{endtime}, '%Y-%m-%d')

SELECT L.activity_id,L.activity_name,L.activity_type,L.activity_person,L.activity_site, L.activity_begin_date,L.activity_video_gatherer from activity_info L
L.activity_name like concat('%',#{search},'%') OR L.activity_video_gatherer like concat('%',#{search},'%') OR L.activity_person like concat('%',#{search},'%')
and date_format(L.activity_begin_date, '%Y-%m-%d') = #{logdate}
and L.activity_begin_date BETWEEN str_to_date(#{starttime}, '%Y-%m-%d') AND str_to_date(#{endtime}, '%Y-%m-%d')
order by L.activity_begin_date desc limit #{start},#{length}

mapper.update:
update activity_info
activity_name = #{activityName}, activity_type = #{activityType}, activity_person = #{activityPerson}, activity_site = #{activitySite}, activity_begin_date = #{activityBeginDate}, activity_video_gatherer = #{activityVideoGatherer}
where activity_id = #{activityId}

mapper.select:
SELECT a.activity_id, a.activity_name, a.activity_type, a.activity_person, a.activity_site, a.activity_begin_date, a.activity_video_gatherer FROM activity_info a WHERE activity_id IN ( SELECT avr.activity_id FROM activity_video_rel AS avr WHERE avr.video_id IN ( SELECT video_id FROM ( SELECT vtr.video_id, GROUP_CONCAT( vt.tag_name ) AS tag_names FROM video_tag_rel vtr, video_tag vt WHERE vtr.tag_id = vt.tag_id GROUP BY video_id ) aa WHERE
aa.tag_names LIKE concat('%',#{tagName},'%')
) ) ORDER BY a.activity_begin_date DESC
