mapper.select:
		SELECT count(1)
        FROM activity_info
        
            
                activity_name = #{activityName}
            
            
                and date_format(activity_begin_date, '%Y-%m-%d') = #{logdate}
            
            
                and activity_begin_date BETWEEN str_to_date(#{starttime}, '%Y-%m-%d') AND str_to_date(#{endtime},
                '%Y-%m-%d')

SELECT L.activity_name,L.activity_type,L.activity_person,L.activity_site, L.activity_begin_date,L.activity_video_gatherer from activity_info L
L.activity_name like concat('%',#{search},'%')
and date_format(L.activity_begin_date, '%Y-%m-%d') = #{logdate}
and L.activity_begin_date BETWEEN str_to_date(#{starttime}, '%Y-%m-%d') AND str_to_date(#{endtime}, '%Y-%m-%d')
order by L.activity_begin_date limit #{start},#{length}
