mapper.insert:
insert into activity_video (video_id, video_address, video_pic_address,video_upload_time)
values (replace(uuid(),'-',''), #{videoAddress}
, #{videoPicAddress}, NOW())

mapper.select:
SELECT V.video_id,V.video_address,V.video_pic_address,V.video_upload_time from activity_video V where V.video_id in ( SELECT R.video_id FROM activity_video_rel R
R.activity_id = #{activityId}
) ORDER BY video_upload_time DESC
